//need to first download the library "react-phone-number-input"
import { useState } from "react";
import PhoneInput from "react-phone-number-input";
import "react-phone-number-input/style.css"; 

const CustomerEntryForm = () => {
    const [firstName, setfirstName] = useState('');
    const [middleInitial, setmiddleInitial] = useState('');
    const [lastName, setLastName] = useState('');
    const [payment, setPayment] = useState("Select");
    const [dob, setDob] = useState(null);
    const [phoneNumber, setPhoneNumber] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault(); 
        const customer = {firstName, middleInitial, lastName, dob, phoneNumber, payment};

        console.log(customer);
    }

    return (
        <div className="customerEntryForm">
            <h2>Customer Information Sheet</h2>
            <form onSubmit={handleSubmit} >
                <label>First Name</label>
                <input 
                    type="text"
                    required
                    value={firstName}
                    onChange={(e) => setfirstName(e.target.value)}
                />
                <label>Middle Initial</label>
                <input 
                    type = "text"
                    maxLength = {1}
                    value = {middleInitial}
                    onChange = { (e) => setmiddleInitial(e.target.value)}
                />
                <label>Last Name</label>
                <input 
                    type="text"
                    required
                    value = {lastName}
                    onChange={ (e) => setLastName(e.target.value)}
                />
                <label>Day of Birth </label>
                <input 
                    type ="date"
                    required 
                    selected = {dob}
                    onChange = { (date) => setDob(date)}
                />
                <label>Method of Payment</label>
                <select
                    value = {payment}
                    onChange = { (e) => setPayment(e.target.value)}
                >
                    <option value="Select">Select</option>
                    <option value="Visa">Visa</option>
                    <option value="Mastercard">Mastercard</option>
                    <option value="American Express">American Express</option>
                    
                </select>
                <label>Phone Number</label>
                <PhoneInput
                    placeholder="Enter phone number"
                    value = {phoneNumber}
                    onChange = {setPhoneNumber}
                />
                <input 
                />
                <button>Submit</button>
            </form>
        </div>
    );
}

export default CustomerEntryForm;
