import React, { useState, useEffect } from 'react';
import './landing-page.css';

function Landing() {
  // Load the sidebar state from localStorage (or default to false)
  const [sidebarCollapsed, setSidebarCollapsed] = useState(
    JSON.parse(localStorage.getItem('sidebarCollapsed')) || false
  );

  // Update localStorage whenever the sidebar state changes
  useEffect(() => {
    localStorage.setItem('sidebarCollapsed', JSON.stringify(sidebarCollapsed));
  }, [sidebarCollapsed]);

  const toggleSidebar = () => {
    setSidebarCollapsed(!sidebarCollapsed);
  };

  return (
    <div className="container">
      {/* Top Navigation Bar */}
      <div className="top-nav">
        <button className="toggle-sidebar" onClick={toggleSidebar}>
          Toggle Sidebar
        </button>
        <div className="logo">RetailPro</div>
      </div>

      {/* Categories Bar */}
      <div className="categories-bar">
        <div className="category-tab active">All Products</div>
        <div className="category-tab">Electronics</div>
      </div>

      {/* Main Area */}
      <div className="main-area">
        {/* Sidebar */}
        <aside className={`sidebar ${sidebarCollapsed ? 'collapsed' : ''}`}>
          <ul className="sidebar-menu">
            <li>Dashboard</li>
            <li>Products</li>
          </ul>
        </aside>

        {/* Main Content */}
        <main className={`main-content ${sidebarCollapsed ? 'full-width' : ''}`}>
          <div className="product-grid">
            <div className="product-card">Product 1</div>
            <div className="product-card">Product 2</div>
          </div>
        </main>
      </div>
    </div>
  );
}

export default Landing;
