import { useState } from "react";


const CustomerEntryForm = () => {
    {/*Variables that the customer would contain*/}
    const [firstName, setfirstName] = useState('');
    const [middleInitial, setmiddleInitial] = useState('');
    const [lastName, setLastName] = useState('');
    const [payment, setPayment] = useState("Select");
    const [dob, setDob] = useState(null);
    const [phoneNumber, setPhoneNumber] = useState('');
    const [aptNum, setAptNum] = useState('');

    {/*Function to place all the values into customer once hit submit */}
    const handleSubmit = (e) => {
        e.preventDefault(); 
        const customer = {firstName, middleInitial, lastName, dob, phoneNumber, payment};

        console.log(customer);
    }

    return (
        <div className="customerEntryForm">
            <h2>Customer Information Sheet</h2>
            <form onSubmit={handleSubmit} >

                {/*First name box */}
                <label>First Name</label>
                <input 
                    type = "text"
                    required
                    maxLength = {20}
                    value = {firstName}
                    onChange={(e) => setfirstName(e.target.value)}
                />

                {/*Middle initial box, would only allow one character */}
                <label>Middle Initial</label>
                <input 
                    type = "text"
                    maxLength = {1}
                    value = {middleInitial}
                    onChange = { (e) => setmiddleInitial(e.target.value)}
                />

                {/*Last name field*/}
                <label>Last Name</label>
                <input 
                    type = "text"
                    required
                    maxLength = {30}
                    value = {lastName}
                    onChange={ (e) => setLastName(e.target.value)}
                />

                {/*Day of birth menu calendar */}
                <label>Day of Birth </label>
                <input 
                    type = "date"
                    required 
                    value = {dob}
                    onChange = { (e) => setDob(e.target.value)}
                />

                {/*Select menu for method of payment */}
                <label>Method of Payment</label>
                <select
                    value = {payment}
                    onChange = { (e) => setPayment(e.target.value)}
                >
                    <option value="Select">Select</option>
                    <option value="Visa">Visa</option>
                    <option value="Mastercard">Mastercard</option>
                    <option value="American Express">American Express</option>
                    
                </select>

                {/*Phone number box, it would only allow in a certain format ###-###-####*/}
                <label>Phone Number</label>
                <input
                    type = "tel"
                    value = {phoneNumber}
                    onChange = {(e) => setPhoneNumber(e.target.value)}
                    pattern = "[0-9]{3}-[0-9]{3}-[0-9]{4}"
                    required
                />

                {/* Email box */}
                <label htmlFor = "email"> Enter your example.com email</label>
                <input
                    type = "email"
                    id = "email"
                    patern = ".+@example\.com"
                    size = "30"
                    required
                />

                {/*Adress components */}
                {/*Apt number */}
                <label>Appartment Number </label>
                <input 
                    type = "number"
                    value = {aptNum}
                    size = "6"
                    onChange = { (e) => setAptNum(e.target.value)}
                />
                <button>Submit</button>
            </form>
        </div>
    );
}

export default CustomerEntryForm;
