            <li><i className="fas fa-home"></i> Dashboard</li>
            <li><i className="fas fa-tag"></i> Products</li>
            <li className="active"><i className="fas fa-shopping-cart"></i> Shopping Cart</li>
            <li><i className="fas fa-users"></i> Customers</li>
            <li><i className="fas fa-chart-bar"></i> Reports</li>
