              <button className="btn-primary continue-shopping"><i className="fas fa-arrow-left"></i> Continue Shopping</button>
            </div>
          </div>

          <div className="cart-container">
            <div className="cart-items">
              {/* Cart Item */}
              <div className="cart-item">
                <div className="item-image">
                  <img src="/api/placeholder/80/80" alt="Wireless Headphones" />
                </div>
                <div className="item-details">
                  <div className="item-name">Wireless Headphones</div>
